import React, { useEffect, useState } from 'react'
import Heading from '../components/Heading'
import API from "../API"
import Select from '../components/Select';

const api = new API();

const Home = () => {
    const [products, setProducts] = useState([]);
    const [selected, setSelected] = useState(null)
    const limit=5
    useEffect(()=>{
        api
            .fetchProducts({limit})
            .then((response)=>{
                setProducts(response.data);
                setSelected(response.data[0].id)
                console.log(response.data)
            })

    }, []);
  return (
    <div>
      <Heading />

      {products.map((product) => (
        //  <Select key={product.id} product={product}/>
        <div className="select" key={product.id} onClick={()=>setSelected(product.id)}>
          <img className="productImg" src={product.image} /><br/>
          <p>{product.title}</p>
        </div>
      ))}
    </div>
  );
}

export default Home
